test_that("testing url_file_func", {
  expect_error(SEC13Flist:::url_file_func(current_year = 0,current_quarter = 0))
  expect_equal(SEC13Flist:::url_file_func(YEAR_=2004, QUARTER_ = 1, current_year = 2019,current_quarter = 3),"https://www.sec.gov/divisions/investment/13f-list.pdf")
  expect_equal(SEC13Flist:::url_file_func(YEAR_=2019, QUARTER_ = 3, current_year = 2019,current_quarter = 3),"https://www.sec.gov/divisions/investment/13f/13flist2019q3.pdf")
  expect_equal(SEC13Flist:::url_file_func(current_year = 2019,current_quarter = 3),"https://www.sec.gov/divisions/investment/13f/13flist2019q3.pdf")
  expect_equal(SEC13Flist:::url_file_func(QUARTER_ = 3, current_year = 2019,current_quarter = 3),"https://www.sec.gov/divisions/investment/13f/13flist2019q3.pdf")
  expect_equal(SEC13Flist:::url_file_func(YEAR_=2019, current_year = 2019,current_quarter = 3),"https://www.sec.gov/divisions/investment/13f/13flist2019q3.pdf")
  expect_equal(SEC13Flist:::url_file_func(YEAR_=2005, QUARTER_ = 4, current_year = 2019,current_quarter = 3),"https://www.sec.gov/divisions/investment/13f/13flist2005q4.pdf")
  expect_error(SEC13Flist:::url_file_func(YEAR_=2019, QUARTER_ = 4, current_year = 2019,current_quarter = 3))
  expect_error(SEC13Flist:::url_file_func(YEAR_=2019, QUARTER_ = 5, current_year = 2019,current_quarter = 3))
  expect_error(SEC13Flist:::url_file_func(YEAR_=2020, QUARTER_ = 4, current_year = 2019,current_quarter = 3))
  expect_error(SEC13Flist:::url_file_func(YEAR_=2003, QUARTER_ = 2, current_year = 2019,current_quarter = 3))
  expect_error(SEC13Flist:::url_file_func(YEAR_=2005, QUARTER_ = 0, current_year = 2019,current_quarter = 3))
  expect_error(SEC13Flist:::url_file_func(YEAR_=0, QUARTER_ = 2, current_year = 2019,current_quarter = 3))
  expect_error(SEC13Flist:::url_file_func(YEAR_="2003a", QUARTER_ = 2, current_year = 2019,current_quarter = 3))
  expect_error(SEC13Flist:::url_file_func(YEAR_=2006, QUARTER_ = "2a", current_year = 2019,current_quarter = 3))
  expect_error(SEC13Flist:::url_file_func(YEAR_="2003a", QUARTER_ = "2b", current_year = 2019,current_quarter = 3))
  expect_error(SEC13Flist:::url_file_func(YEAR_=2007, QUARTER_ = 0, current_year = 2019,current_quarter = 3))

})
